import styled, {css} from "styled-components";
import {Theme} from "../Thems.styled";

type ButtonStyledPropsType = {
	primary?: boolean,
	outlined?: boolean
}
export const ButtonStyled = styled.button<ButtonStyledPropsType>`
  display: flex;
  justify-content: center;
  align-items: center;
  min-width: 86px;
  min-height: 30px;
  border-radius: 5px;
  cursor: pointer;

  //primary
  ${props => props.primary && css<ButtonStyledPropsType>`
    background-color: ${Theme.colors.bgColorBtn};
    color: ${Theme.colors.colorBtnPrimary};
    border: none;
    

  `} //outlined

  ${props => props.outlined && css<ButtonStyledPropsType>`
    background-color: transparent;
    color: ${Theme.colors.bgColorBtn};
    border: 2px solid ${Theme.colors.bgColorBtn};
  `}

`
