import styled from "styled-components";
import {Theme} from "../Thems.styled";

export const Description = styled.div`
  color: ${Theme.colors.second};
  font-style: normal;
  font-family: Inter;
  font-size: 12px;
  font-weight: 500;
  line-height: 20px;
`
